ClientSideScript:
function ClientScript_OnAfterRender(form){
    form.on('change', function(event) { 
        if (event.changed && event.changed.component.key === 'txtEffectiveDate' && event.changed.value) {
            // Add 6 months to the effective date to get the review date
            var effectiveDate = new Date(event.changed.value);
            effectiveDate.setMonth(effectiveDate.getMonth() + 6);
            form.getComponent('txtReviewDate').setValue(effectiveDate);
        }
    });
}

